import React, { Component } from "react";

// *******Child Components*******
import MainContent from "./Components/MainContent";
import SideNav from "./Components/SideNav";

// *******Style Sheet*******
import "./App.css";

export class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      loading: false,
      data: []
    };
  }

  componentDidMount(){
    this.fetchData();
  }

  fetchData = () => {
    this.setState({
      loading: true
    });
    fetch('http://test.boxigo.in/api_assignment.json',{
      method: 'GET',
      mode: 'no-cors'
    }).then((response) => {
      if (response.ok) {
        response.json().then((result) =>{
          this.setState({
            loading: false,
            data: result
          });
        });
      }
    }).catch((err) => {
      console.log(err);
      // this.setstate({
      //   loading: false
      // });
    });
  }





  render() {
    console.log(this.state.data);
    return (
      <div>
        {this.state.loading && <h1>Loading...</h1>}
        <SideNav />
        <MainContent />
      </div>
    );
  }
}

export default App;
