import "./App.css";

export class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      data: [],
    };
  }

  componentDidMount() {
    this.fetchData();
  }

  fetchData = () => {
    this.setState({
      loading: true,
    });
    var oReq = new XMLHttpRequest();

    oReq.onload = function (e) {
      var arraybuffer = oReq.response; // not responseText
      console.log(arraybuffer);
    };
    oReq.open("GET", "http://test.boxigo.in/api_assignment.json");
    oReq.responseType = "arraybuffer";
