import React from "react";
import "./styles.css";

class Room extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      totalItems: "",
      category: {
        furnitures: {},
        electricals: {},
        fragile: {},
      },
    };
  }

  componentDidMount() {
    this.mapData(this.props.data);
  }

  UNSAFE_componentWillReceiveProps(newProps) {
    this.mapData(newProps.data);
  }

  mapData = (data) => {
    const furnitures = data.categories.find(
      (item) => item.categoryID === "furnitures"
    );
    const electricals = data.categories.find(
      (item) => item.categoryID === "electricals"
    );
    const fragile = data.categories.find(
      (item) => item.categoryID === "fragile"
    );

    furnitures.itemsList = furnitures.itemsList.all.filter(
      (item) => item.qty > 0
    );
    electricals.itemsList = electricals.itemsList.all.filter(
      (item) => item.qty > 0
    );
    fragile.itemsList = fragile.itemsList.all.filter((item) => item.qty > 0);

    this.setState({
      title: data.roomName,
      totalItems:
        furnitures.itemsList.length +
        electricals.itemsList.length +
        fragile.itemsList.length,
      category: {
        furnitures,
        electricals,
        fragile,
      },
    });
  };

  render() {
    return (
      <div>
        <div className="room-category">
          <div className="room">
            <h1>{ this.state.title }</h1>
            <div className="circle">{ this.state.totalItems }</div>
          </div>
        </div>
        <div className="room-details">
          <div className="things">
            <h1>Furnitures</h1>
            <div className="items">
              <p>Item</p>
              <p>0</p>
            </div>
            <h3>Material</h3>
          </div>
        </div>

        <div className="house-details">
          <div className="house-details-top">
            <h1>House Details</h1>
            <button className="house-details-btn">Edit house details</button>
          </div>
          <h2>Existing house details</h2>
          <div className="info">
            <div className="prev-house-details">
              <h4>Floor No.</h4>
              <br />
              <p>0</p>
            </div>
            <div className="prev-house-details">
              <h4>Elevator Available</h4>
              <br />
              <p>No</p>
            </div>
            <div className="prev-house-details">
              <h4>Parking Required</h4>
              <br />
              <p>Yes</p>
            </div>
            <div className="prev-house-details">
              <h4>Distance from truck to door</h4>
              <br />
              <p>1 meter</p>
            </div>
          </div>
          <h4>Additional Information</h4>
          <br />
          <p>No additional info</p>
        </div>
        <hr />
        <div className="house-details">
          <h2>New house details</h2>
          <div className="info">
            <div className="new-house-details">
              <h4>Floor No.</h4>
              <br />
              <p>0</p>
            </div>
            <div className="new-house-details">
              <h4>Elevator Available</h4>
              <br />
              <p>No</p>
            </div>
            <div className="new-house-details">
              <h4>Parking Required</h4>
              <br />
              <p>Yes</p>
            </div>
            <div className="new-house-details">
              <h4>Distance from truck to door</h4>
              <br />
              <p>1 meter</p>
            </div>
          </div>
          <h4>Additional Information</h4>
          <br />
          <p>No additional info</p>
        </div>
      </div>
    );
  }
}



export default Room;
